import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import path from "path";
import runtimeErrorOverlay from "@replit/vite-plugin-runtime-error-modal";

export default defineConfig({
  plugins: [
    react(),
    runtimeErrorOverlay(),
    ...(process.env.NODE_ENV !== "production" &&
    process.env.REPL_ID !== undefined
      ? [
          await import("@replit/vite-plugin-cartographer").then((m) =>
            m.cartographer(),
          ),
        ]
      : []),
  ],
  resolve: {
    alias: {
      "@": path.resolve(import.meta.dirname, "client", "src"),
      "@shared": path.resolve(import.meta.dirname, "shared"),
      "@assets": path.resolve(import.meta.dirname, "attached_assets"),
    },
  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { 
  insertUserSchema, insertReportSchema, insertNoticeSchema, insertVisitorSchema,
  insertOccurrenceSchema, insertChecklistItemSchema, insertDrillSchema, 
  insertCampaignSchema, insertEmergencyAlertSchema,
  updateUserSchema, updateNoticeSchema, updateCampaignSchema, 
  updateChecklistItemSchema, reportStatusSchema
} from "@shared/schema";

export async function registerRoutes(app: Express): Promise<Server> {
  // Helper function for error handling
  const handleAsync = (fn: Function) => (req: any, res: any, next: any) => {
    Promise.resolve(fn(req, res, next)).catch(next);
  };

  // Helper function for validation
  const validateBody = (schema: any) => (req: any, res: any, next: any) => {
    try {
      req.validatedBody = schema.parse(req.body);
      next();
    } catch (error: any) {
      res.status(400).json({ error: "Validation failed", details: error.errors });
    }
  };

  // Authentication middleware
  const requireAuth = () => (req: any, res: any, next: any) => {
    const userId = req.headers['x-user-id'];
    if (!userId) {
      return res.status(401).json({ error: "Authentication required" });
    }
    req.userId = userId;
    next();
  };

  // Helper to verify user exists
  const verifyUser = async (userId: string) => {
    const user = await storage.getUser(userId);
    if (!user || !user.isActive) {
      return null;
    }
    return user;
  };

  // Role-based authorization middleware
  const requireRole = (allowedRoles: string[]) => (req: any, res: any, next: any) => {
    if (!req.user || !allowedRoles.includes(req.user.role)) {
      return res.status(403).json({ error: "Forbidden: insufficient permissions" });
    }
    next();
  };

  // Users API
  app.get("/api/users", handleAsync(async (req: any, res: any) => {
    const users = await storage.getAllUsers();
    res.json(users);
  }));

  app.get("/api/users/:id", handleAsync(async (req: any, res: any) => {
    const user = await storage.getUser(req.params.id);
    if (!user) {
      return res.status(404).json({ error: "User not found" });
    }
    res.json(user);
  }));

  app.post("/api/users", validateBody(insertUserSchema), handleAsync(async (req: any, res: any) => {
    const user = await storage.createUser(req.validatedBody);
    res.status(201).json(user);
  }));

  app.patch("/api/users/:id", requireAuth(), validateBody(updateUserSchema), handleAsync(async (req: any, res: any) => {
    const user = await verifyUser(req.userId);
    if (!user) {
      return res.status(401).json({ error: "Invalid user" });
    }

    // Users can only update their own profile, or admin can update anyone
    if (req.params.id !== req.userId && user.role !== 'admin') {
      return res.status(403).json({ error: "Forbidden: can only update own profile" });
    }

    // Prevent role escalation - only admin can change roles
    if (req.validatedBody.role && user.role !== 'admin') {
      return res.status(403).json({ error: "Forbidden: cannot change role" });
    }

    const updatedUser = await storage.updateUser(req.params.id, req.validatedBody);
    if (!updatedUser) {
      return res.status(404).json({ error: "User not found" });
    }
    res.json(updatedUser);
  }));

  // Reports API
  app.get("/api/reports", handleAsync(async (req: any, res: any) => {
    const reports = await storage.getReports();
    res.json(reports);
  }));

  app.post("/api/reports", validateBody(insertReportSchema), handleAsync(async (req: any, res: any) => {
    // Get user ID from Firebase token or session (simplified for now)
    const reporterId = req.headers['x-user-id'] || undefined;
    const report = await storage.createReport({
      ...req.validatedBody,
      reporterId: req.validatedBody.isAnonymous ? undefined : reporterId
    });
    res.status(201).json(report);
  }));

  app.patch("/api/reports/:id/status", requireAuth(), validateBody(reportStatusSchema), handleAsync(async (req: any, res: any) => {
    const user = await verifyUser(req.userId);
    if (!user) {
      return res.status(401).json({ error: "Invalid user" });
    }

    // Only staff and admin can update report status
    if (user.role === 'student') {
      return res.status(403).json({ error: "Forbidden: insufficient permissions" });
    }

    const report = await storage.updateReportStatus(req.params.id, req.validatedBody.status);
    if (!report) {
      return res.status(404).json({ error: "Report not found" });
    }
    res.json(report);
  }));

  // Notices API
  app.get("/api/notices", handleAsync(async (req: any, res: any) => {
    const notices = await storage.getActiveNotices();
    res.json(notices);
  }));

  app.post("/api/notices", requireAuth(), validateBody(insertNoticeSchema), handleAsync(async (req: any, res: any) => {
    const user = await verifyUser(req.userId);
    if (!user) {
      return res.status(401).json({ error: "Invalid user" });
    }
    const notice = await storage.createNotice({
      ...req.validatedBody,
      createdBy: req.userId
    });
    res.status(201).json(notice);
  }));

  app.patch("/api/notices/:id", requireAuth(), validateBody(updateNoticeSchema), handleAsync(async (req: any, res: any) => {
    const user = await verifyUser(req.userId);
    if (!user) {
      return res.status(401).json({ error: "Invalid user" });
    }

    // Only staff and admin can update notices
    if (user.role === 'student') {
      return res.status(403).json({ error: "Forbidden: insufficient permissions" });
    }

    const notice = await storage.updateNotice(req.params.id, req.validatedBody);
    if (!notice) {
      return res.status(404).json({ error: "Notice not found" });
    }
    res.json(notice);
  }));

  // Visitors API
  app.get("/api/visitors", handleAsync(async (req: any, res: any) => {
    const visitors = await storage.getVisitors();
    res.json(visitors);
  }));

  app.get("/api/visitors/active", handleAsync(async (req: any, res: any) => {
    const visitors = await storage.getActiveVisitors();
    res.json(visitors);
  }));

  app.post("/api/visitors", requireAuth(), validateBody(insertVisitorSchema), handleAsync(async (req: any, res: any) => {
    const user = await verifyUser(req.userId);
    if (!user) {
      return res.status(401).json({ error: "Invalid user" });
    }
    const visitor = await storage.createVisitor({
      ...req.validatedBody,
      registeredBy: req.userId
    });
    res.status(201).json(visitor);
  }));

  app.patch("/api/visitors/:id/checkout", requireAuth(), handleAsync(async (req: any, res: any) => {
    const user = await verifyUser(req.userId);
    if (!user) {
      return res.status(401).json({ error: "Invalid user" });
    }

    const visitor = await storage.checkOutVisitor(req.params.id);
    if (!visitor) {
      return res.status(404).json({ error: "Visitor not found" });
    }
    res.json(visitor);
  }));

  // Occurrences API
  app.get("/api/occurrences", handleAsync(async (req: any, res: any) => {
    const occurrences = await storage.getOccurrences();
    res.json(occurrences);
  }));

  app.post("/api/occurrences", requireAuth(), validateBody(insertOccurrenceSchema), handleAsync(async (req: any, res: any) => {
    const user = await verifyUser(req.userId);
    if (!user) {
      return res.status(401).json({ error: "Invalid user" });
    }
    const occurrence = await storage.createOccurrence({
      ...req.validatedBody,
      createdBy: req.userId
    });
    res.status(201).json(occurrence);
  }));

  // Checklist Items API
  app.get("/api/checklist", handleAsync(async (req: any, res: any) => {
    const items = await storage.getChecklistItems();
    res.json(items);
  }));

  app.post("/api/checklist", requireAuth(), validateBody(insertChecklistItemSchema), handleAsync(async (req: any, res: any) => {
    const user = await verifyUser(req.userId);
    if (!user) {
      return res.status(401).json({ error: "Invalid user" });
    }

    // Only staff and admin can create checklist items
    if (user.role === 'student') {
      return res.status(403).json({ error: "Forbidden: insufficient permissions" });
    }

    const item = await storage.createChecklistItem(req.validatedBody);
    res.status(201).json(item);
  }));

  app.patch("/api/checklist/:id", validateBody(updateChecklistItemSchema), handleAsync(async (req: any, res: any) => {
    let updates = { ...req.validatedBody };

    if (req.validatedBody.isCompleted !== undefined) {
      const userId = req.headers['x-user-id'];
      if (!userId) {
        return res.status(401).json({ error: "Authentication required to complete items" });
      }

      const user = await verifyUser(userId);
      if (!user) {
        return res.status(401).json({ error: "Invalid user" });
      }

      if (req.validatedBody.isCompleted) {
        updates = {
          ...updates,
          completedBy: userId,
          completedAt: new Date()
        };
      } else {
        updates = {
          ...updates,
          completedBy: null,
          completedAt: null
        };
      }
    }

    const item = await storage.updateChecklistItem(req.params.id, updates);
    if (!item) {
      return res.status(404).json({ error: "Checklist item not found" });
    }
    res.json(item);
  }));

  // Drills API
  app.get("/api/drills", handleAsync(async (req: any, res: any) => {
    const drills = await storage.getDrills();
    res.json(drills);
  }));

  app.get("/api/drills/upcoming", handleAsync(async (req: any, res: any) => {
    const drills = await storage.getUpcomingDrills();
    res.json(drills);
  }));

  app.post("/api/drills", requireAuth(), validateBody(insertDrillSchema), handleAsync(async (req: any, res: any) => {
    const user = await verifyUser(req.userId);
    if (!user) {
      return res.status(401).json({ error: "Invalid user" });
    }
    const drill = await storage.createDrill({
      ...req.validatedBody,
      createdBy: req.userId
    });
    res.status(201).json(drill);
  }));

  // Campaigns API
  app.get("/api/campaigns", handleAsync(async (req: any, res: any) => {
    const campaigns = await storage.getActiveCampaigns();
    res.json(campaigns);
  }));

  app.post("/api/campaigns", requireAuth(), validateBody(insertCampaignSchema), handleAsync(async (req: any, res: any) => {
    const user = await verifyUser(req.userId);
    if (!user) {
      return res.status(401).json({ error: "Invalid user" });
    }
    const campaign = await storage.createCampaign({
      ...req.validatedBody,
      createdBy: req.userId
    });
    res.status(201).json(campaign);
  }));

  app.patch("/api/campaigns/:id", requireAuth(), validateBody(updateCampaignSchema), handleAsync(async (req: any, res: any) => {
    const user = await verifyUser(req.userId);
    if (!user) {
      return res.status(401).json({ error: "Invalid user" });
    }

    // Only staff and admin can update campaigns
    if (user.role === 'student') {
      return res.status(403).json({ error: "Forbidden: insufficient permissions" });
    }

    const campaign = await storage.updateCampaign(req.params.id, req.validatedBody);
    if (!campaign) {
      return res.status(404).json({ error: "Campaign not found" });
    }
    res.json(campaign);
  }));

  // Emergency Alerts API
  app.get("/api/emergency-alerts", handleAsync(async (req: any, res: any) => {
    const alerts = await storage.getActiveEmergencyAlerts();
    res.json(alerts);
  }));

  app.post("/api/emergency-alerts", requireAuth(), validateBody(insertEmergencyAlertSchema), handleAsync(async (req: any, res: any) => {
    const user = await verifyUser(req.userId);
    if (!user) {
      return res.status(401).json({ error: "Invalid user" });
    }
    const alert = await storage.createEmergencyAlert({
      ...req.validatedBody,
      triggeredBy: req.userId
    });
    res.status(201).json(alert);
  }));

  app.patch("/api/emergency-alerts/:id/resolve", requireAuth(), handleAsync(async (req: any, res: any) => {
    const user = await verifyUser(req.userId);
    if (!user) {
      return res.status(401).json({ error: "Invalid user" });
    }
    const alert = await storage.resolveEmergencyAlert(req.params.id, req.userId);
    if (!alert) {
      return res.status(404).json({ error: "Emergency alert not found" });
    }
    res.json(alert);
  }));

  // Dashboard stats API
  app.get("/api/dashboard/stats", handleAsync(async (req: any, res: any) => {
    const [users, reports, activeVisitors, activeAlerts, upcomingDrills] = await Promise.all([
      storage.getAllUsers(),
      storage.getReports(),
      storage.getActiveVisitors(),
      storage.getActiveEmergencyAlerts(),
      storage.getUpcomingDrills()
    ]);

    const stats = {
      totalUsers: users.length,
      pendingReports: reports.filter(r => r.status === 'pending').length,
      activeVisitors: activeVisitors.length,
      activeAlerts: activeAlerts.length,
      upcomingDrills: upcomingDrills.length,
      recentReports: reports.slice(-5).reverse()
    };

    res.json(stats);
  }));

  // Error handling middleware
  app.use((error: any, req: any, res: any, next: any) => {
    console.error('API Error:', error);
    res.status(500).json({ 
      error: 'Internal server error',
      message: error.message 
    });
  });

  const httpServer = createServer(app);

  return httpServer;
}

server/storage.ts.content:
import { 
  type User, type InsertUser, type Report, type InsertReport,
  type Notice, type InsertNotice, type Visitor, type InsertVisitor,
  type Occurrence, type InsertOccurrence, type ChecklistItem, type InsertChecklistItem,
  type Drill, type InsertDrill, type Campaign, type InsertCampaign,
  type EmergencyAlert, type InsertEmergencyAlert
} from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // Users
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  getUserByFirebaseUid(firebaseUid: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: string, updates: Partial<User>): Promise<User | undefined>;
  getAllUsers(): Promise<User[]>;

  // Reports
  createReport(report: InsertReport & { reporterId?: string }): Promise<Report>;
  getReports(): Promise<Report[]>;
  getReport(id: string): Promise<Report | undefined>;
  updateReportStatus(id: string, status: string): Promise<Report | undefined>;

  // Notices
  createNotice(notice: InsertNotice & { createdBy: string }): Promise<Notice>;
  getActiveNotices(): Promise<Notice[]>;
  updateNotice(id: string, updates: Partial<Notice>): Promise<Notice | undefined>;

  // Visitors
  createVisitor(visitor: InsertVisitor & { registeredBy: string }): Promise<Visitor>;
  getActiveVisitors(): Promise<Visitor[]>;
  getVisitors(): Promise<Visitor[]>;
  checkOutVisitor(id: string): Promise<Visitor | undefined>;

  // Occurrences
  createOccurrence(occurrence: InsertOccurrence & { createdBy: string }): Promise<Occurrence>;
  getOccurrences(): Promise<Occurrence[]>;

  // Checklist Items
  createChecklistItem(item: InsertChecklistItem): Promise<ChecklistItem>;
  getChecklistItems(): Promise<ChecklistItem[]>;
  updateChecklistItem(id: string, updates: Partial<ChecklistItem>): Promise<ChecklistItem | undefined>;

  // Drills
  createDrill(drill: InsertDrill & { createdBy: string }): Promise<Drill>;
  getDrills(): Promise<Drill[]>;
  getUpcomingDrills(): Promise<Drill[]>;

  // Campaigns
  createCampaign(campaign: InsertCampaign & { createdBy: string }): Promise<Campaign>;
  getActiveCampaigns(): Promise<Campaign[]>;
  updateCampaign(id: string, updates: Partial<Campaign>): Promise<Campaign | undefined>;

  // Emergency Alerts
  createEmergencyAlert(alert: InsertEmergencyAlert & { triggeredBy: string }): Promise<EmergencyAlert>;
  getActiveEmergencyAlerts(): Promise<EmergencyAlert[]>;
  resolveEmergencyAlert(id: string, resolvedBy: string): Promise<EmergencyAlert | undefined>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private reports: Map<string, Report>;
  private notices: Map<string, Notice>;
  private visitors: Map<string, Visitor>;
  private occurrences: Map<string, Occurrence>;
  private checklistItems: Map<string, ChecklistItem>;
  private drills: Map<string, Drill>;
  private campaigns: Map<string, Campaign>;
  private emergencyAlerts: Map<string, EmergencyAlert>;

  constructor() {
    this.users = new Map();
    this.reports = new Map();
    this.notices = new Map();
    this.visitors = new Map();
    this.occurrences = new Map();
    this.checklistItems = new Map();
    this.drills = new Map();
    this.campaigns = new Map();
    this.emergencyAlerts = new Map();
  }

  // Users
  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(user => user.email === email);
  }

  async getUserByFirebaseUid(firebaseUid: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(user => user.firebaseUid === firebaseUid);
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { 
      ...insertUser, 
      role: (insertUser.role as "student" | "staff" | "admin") ?? "student",
      id, 
      isActive: true,
      createdAt: new Date()
    };
    this.users.set(id, user);
    return user;
  }

  async updateUser(id: string, updates: Partial<User>): Promise<User | undefined> {
    const user = this.users.get(id);
    if (!user) return undefined;

    const updatedUser = { ...user, ...updates };
    this.users.set(id, updatedUser);
    return updatedUser;
  }

  async getAllUsers(): Promise<User[]> {
    return Array.from(this.users.values());
  }

  // Reports
  async createReport(reportData: InsertReport & { reporterId?: string }): Promise<Report> {
    const id = randomUUID();
    const report: Report = {
      ...reportData,
      isAnonymous: reportData.isAnonymous ?? false,
      id,
      reporterId: reportData.reporterId || null,
      status: "pending",
      createdAt: new Date()
    };
    this.reports.set(id, report);
    return report;
  }

  async getReports(): Promise<Report[]> {
    return Array.from(this.reports.values());
  }

  async getReport(id: string): Promise<Report | undefined> {
    return this.reports.get(id);
  }

  async updateReportStatus(id: string, status: string): Promise<Report | undefined> {
    const report = this.reports.get(id);
    if (!report) return undefined;

    const updatedReport = { ...report, status };
    this.reports.set(id, updatedReport);
    return updatedReport;
  }

  // Notices
  async createNotice(noticeData: InsertNotice & { createdBy: string }): Promise<Notice> {
    const id = randomUUID();
    const notice: Notice = {
      ...noticeData,
      id,
      isActive: true,
      createdAt: new Date()
    };
    this.notices.set(id, notice);
    return notice;
  }

  async getActiveNotices(): Promise<Notice[]> {
    return Array.from(this.notices.values()).filter(notice => notice.isActive);
  }

  async updateNotice(id: string, updates: Partial<Notice>): Promise<Notice | undefined> {
    const notice = this.notices.get(id);
    if (!notice) return undefined;

    const updatedNotice = { ...notice, ...updates };
    this.notices.set(id, updatedNotice);
    return updatedNotice;
  }

  // Visitors
  async createVisitor(visitorData: InsertVisitor & { registeredBy: string }): Promise<Visitor> {
    const id = randomUUID();
    const visitor: Visitor = {
      ...visitorData,
      id,
      entryTime: new Date(),
      exitTime: null
    };
    this.visitors.set(id, visitor);
    return visitor;
  }

  async getActiveVisitors(): Promise<Visitor[]> {
    return Array.from(this.visitors.values()).filter(visitor => !visitor.exitTime);
  }

  async getVisitors(): Promise<Visitor[]> {
    return Array.from(this.visitors.values());
  }

  async checkOutVisitor(id: string): Promise<Visitor | undefined> {
    const visitor = this.visitors.get(id);
    if (!visitor) return undefined;

    const updatedVisitor = { ...visitor, exitTime: new Date() };
    this.visitors.set(id, updatedVisitor);
    return updatedVisitor;
  }

  // Occurrences
  async createOccurrence(occurrenceData: InsertOccurrence & { createdBy: string }): Promise<Occurrence> {
    const id = randomUUID();
    const occurrence: Occurrence = {
      ...occurrenceData,
      severity: occurrenceData.severity ?? "medium",
      id,
      createdAt: new Date()
    };
    this.occurrences.set(id, occurrence);
    return occurrence;
  }

  async getOccurrences(): Promise<Occurrence[]> {
    return Array.from(this.occurrences.values());
  }

  // Checklist Items
  async createChecklistItem(itemData: InsertChecklistItem): Promise<ChecklistItem> {
    const id = randomUUID();
    const item: ChecklistItem = {
      ...itemData,
      description: itemData.description ?? null,
      id,
      isCompleted: false,
      completedBy: null,
      completedAt: null,
      createdAt: new Date()
    };
    this.checklistItems.set(id, item);
    return item;
  }

  async getChecklistItems(): Promise<ChecklistItem[]> {
    return Array.from(this.checklistItems.values());
  }

  async updateChecklistItem(id: string, updates: Partial<ChecklistItem>): Promise<ChecklistItem | undefined> {
    const item = this.checklistItems.get(id);
    if (!item) return undefined;

    const updatedItem = { ...item, ...updates };
    this.checklistItems.set(id, updatedItem);
    return updatedItem;
  }

  // Drills
  async createDrill(drillData: InsertDrill & { createdBy: string }): Promise<Drill> {
    const id = randomUUID();
    const drill: Drill = {
      ...drillData,
      type: drillData.type ?? "evacuation",
      description: drillData.description ?? null,
      id,
      createdAt: new Date()
    };
    this.drills.set(id, drill);
    return drill;
  }

  async getDrills(): Promise<Drill[]> {
    return Array.from(this.drills.values());
  }

  async getUpcomingDrills(): Promise<Drill[]> {
    const now = new Date();
    return Array.from(this.drills.values()).filter(drill => drill.scheduledDate > now);
  }

  // Campaigns
  async createCampaign(campaignData: InsertCampaign & { createdBy: string }): Promise<Campaign> {
    const id = randomUUID();
    const campaign: Campaign = {
      ...campaignData,
      id,
      isActive: true,
      createdAt: new Date()
    };
    this.campaigns.set(id, campaign);
    return campaign;
  }

  async getActiveCampaigns(): Promise<Campaign[]> {
    return Array.from(this.campaigns.values()).filter(campaign => campaign.isActive);
  }

  async updateCampaign(id: string, updates: Partial<Campaign>): Promise<Campaign | undefined> {
    const campaign = this.campaigns.get(id);
    if (!campaign) return undefined;

    const updatedCampaign = { ...campaign, ...updates };
    this.campaigns.set(id, updatedCampaign);
    return updatedCampaign;
  }

  // Emergency Alerts
  async createEmergencyAlert(alertData: InsertEmergencyAlert & { triggeredBy: string }): Promise<EmergencyAlert> {
    const id = randomUUID();
    const alert: EmergencyAlert = {
      ...alertData,
      location: alertData.location ?? null,
      id,
      isResolved: false,
      resolvedBy: null,
      resolvedAt: null,
      createdAt: new Date()
    };
    this.emergencyAlerts.set(id, alert);
    return alert;
  }

  async getActiveEmergencyAlerts(): Promise<EmergencyAlert[]> {
    return Array.from(this.emergencyAlerts.values()).filter(alert => !alert.isResolved);
  }

  async resolveEmergencyAlert(id: string, resolvedBy: string): Promise<EmergencyAlert | undefined> {
    const alert = this.emergencyAlerts.get(id);
    if (!alert) return undefined;

    const updatedAlert = { 
      ...alert, 
      isResolved: true, 
      resolvedBy, 
      resolvedAt: new Date() 
    };
    this.emergencyAlerts.set(id, updatedAlert);
    return updatedAlert;
  }
}

export const storage = new MemStorage();

