        { title: 'Denunciar', url: '/reports', icon: AlertTriangle, roles: ['student', 'staff', 'admin'] },
        { title: 'Anti-Bullying', url: '/bullying', icon: Shield, roles: ['student', 'staff', 'admin'] },
        { title: 'EmergÃªncia', url: '/emergency', icon: AlertCircle, roles: ['student', 'staff', 'admin'] },
      ]
    },
    {
      title: 'InformaÃ§Ãµes',
      items: [
        { title: 'Avisos Urgentes', url: '/notices', icon: Bell, roles: ['student', 'staff', 'admin'] },
        { title: 'Plano EvacuaÃ§Ã£o', url: '/evacuation', icon: Map, roles: ['student', 'staff', 'admin'] },
        { title: 'Campanhas', url: '/campaigns', icon: BookOpen, roles: ['student', 'staff', 'admin'] },
        { title: 'Contatos Ãšteis', url: '/contacts', icon: Phone, roles: ['student', 'staff', 'admin'] },
      ]
    },
    {
      title: 'GestÃ£o',
      items: [
        { title: 'Visitantes', url: '/visitors', icon: Users, roles: ['staff', 'admin'] },
        { title: 'OcorrÃªncias', url: '/occurrences', icon: FileText, roles: ['staff', 'admin'] },
        { title: 'VigilÃ¢ncia', url: '/surveillance', icon: Camera, roles: ['staff', 'admin'] },
        { title: 'Checklist', url: '/checklist', icon: CheckSquare, roles: ['staff', 'admin'] },
        { title: 'Simulados', url: '/drills', icon: Calendar, roles: ['staff', 'admin'] },
      ]
    },
    {
      title: 'AdministraÃ§Ã£o',
      items: [
        { title: 'Gerenciar UsuÃ¡rios', url: '/users', icon: Users, roles: ['admin'] },
        { title: 'RelatÃ³rios', url: '/admin/reports', icon: FileText, roles: ['admin'] },
      ]
    },
  ];

  const filteredNavigation = navigationItems.map(section => ({
    ...section,
    items: section.items.filter(item => 
      hasPermission(item.roles as any)
    )
  })).filter(section => section.items.length > 0);

  const sidebarStyle = {
    '--sidebar-width': '16rem',
    '--sidebar-width-icon': '3rem',
  };

  return (
    <SidebarProvider style={sidebarStyle as React.CSSProperties}>
      <div className="flex h-screen w-full">
        <Sidebar className="border-r">
          <SidebarHeader className="border-b p-4">
            <div className="flex items-center gap-2">
              <div className="p-2 bg-primary/10 rounded-lg">
                <Shield className="h-6 w-6 text-primary" />
              </div>
              <div>
                <h2 className="text-lg font-semibold">Escola Segura</h2>
                <p className="text-xs text-muted-foreground capitalize">
                  {user?.role === USER_ROLES.ADMIN ? 'DireÃ§Ã£o' : 
                   user?.role === USER_ROLES.STAFF ? 'FuncionÃ¡rio' : 'Aluno'}
                </p>
              </div>
            </div>
          </SidebarHeader>

          <SidebarContent className="flex-1 overflow-auto">
            {filteredNavigation.map((section) => (
              <SidebarGroup key={section.title}>
                <SidebarGroupLabel>{section.title}</SidebarGroupLabel>
                <SidebarGroupContent>
                  <SidebarMenu>
                    {section.items.map((item) => (
                      <SidebarMenuItem key={item.url}>
                        <SidebarMenuButton asChild isActive={location === item.url}>
                          <Link href={item.url} data-testid={`nav-${item.title.toLowerCase().replace(/\\s+/g, '-')}`}>
