  });

  const toggleCampaignMutation = useMutation({
    mutationFn: ({ id, isActive }: { id: string; isActive: boolean }) =>
      apiRequest("PATCH", `/api/campaigns/${id}`, {
        isActive,
        headers: { "x-user-id": user?.id || "" }
