    onError: (error: any) => {
      toast({
        title: "Erro",
        description: error.message || "Falha ao criar item de checklist.",
        variant: "destructive",
      });
    },
  });

  const toggleItemMutation = useMutation({
    mutationFn: ({ id, isCompleted }: { id: string; isCompleted: boolean }) =>
      apiRequest("PATCH", `/api/checklist/${id}`, {
