            </DialogTrigger>
            <DialogContent className="sm:max-w-[425px]">
              <DialogHeader>
                <DialogTitle>Adicionar Item ao Checklist</DialogTitle>
                <DialogDescription>
                  Crie um novo item de verificaÃ§Ã£o de seguranÃ§a.
                </DialogDescription>
              </DialogHeader>
              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                  <FormField
                    control={form.control}
                    name="title"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>TÃ­tulo do Item</FormLabel>
                        <FormControl>
                          <Input 
                            data-testid="input-checklist-title"
                            placeholder="Ex: Verificar extintores de incÃªndio" 
                            {...field} 
                          />
